import './style.scss';
import { Header } from './Header';
import { Tr } from './Tr';
import { useState } from 'react';
import { data } from './request';
import removeItem from './request';

const aux = data;

function App() {
  const [cart, setCart] = useState(aux);

  const updateQty = (id, qty)=>{
    setCart(cartValue=>{
      cartValue.forEach(el=> {

        el.qty += qty
      })
      return cartValue;
    });
    console.log(cart)
  }

  const handleRemoveItem = (id) =>{
    setCart(auxCart=>{
      return auxCart.filter(e=>{
        return e._id !== id;
      });
    });
  }

  return (
    <>
      <Header />
      <main>
        <header>
          <i className='bx bxs-cart-alt'></i>
          <h1>Carrinho de compras</h1>
        </header>
        <div className="container">

          <table>
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Qntd.</th>
                <th>Preço</th>
                <th>Subtotal</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {cart.map(item=>(
                <Tr update={updateQty} cancel={handleRemoveItem} data={item} key={item._id}/>
              ))}
            </tbody>
          </table>
          <footer>
            <div id="frete">
              <input type="text"/>
              <button>Calcular frete</button>
            </div>
            <div id="total">
              <div className="subtotal">
                <span>Subtotal</span>
                <span>R$ 393,45</span>
              </div>
              <div className="cupom">
                <button><span>Adicione um cupom <i className='bx bx-right-arrow-alt'></i></span></button>
              </div>
            </div>
          </footer>
        </div>
        <footer className="main-footer">
          <button>Escolher mais produtos</button>
          <button>Finalizar compra</button>
        </footer>
      </main>
      <footer id="page-footer">
        <div className="container-footer">
          <div className="creators">
            <h1>Criadores</h1>
            <span>José Carlos Eliodoro de Santana</span>
          </div>
          <div className="contact">
            <h1>Contatos</h1>
            <span>Linkedin</span>
          </div>
        </div>
      </footer>
    </>
  );
}

export default App;
